mapper.select:
		select * from bill where 1=1

            	and payer = #{payer}


            	and payee = #{payee}


            	and billId = #{billId}


            	and billType = #{billType}


            	and transDate = #{transDate}


            	and state = #{state}


            	and orderJnlno = #{orderJnlno}


        	 	and remoteJnlNo = #{remoteJnlNo}

        	 order by transtime desc

mapper.update:
		update bill set 

            	state=#{state} ,


            	remoteJnlNo=#{remoteJnlNo}

		where billId = #{billId}
