mapper.update:
update community set
		isenable   = 2,
		modifyBy   = #{modifyBy},
		modifyTime = #{modifyTime}
where comid = #{comid}

mapper.select:
		select * from community where 1=1

            	and comid = #{comid}


            	and zoneid = #{zoneid}


            	and comname like '%'||#{comname}||'%'


            	and address like '%'||#{address}||'%'


            	and isenable = #{isenable}


            	 and createTime >= #{createTime} 

        	 order by createTime desc
