mapper.select:
		select * from ParkingSpaceBill where 1=1

            	and orderJnlNo = #{orderJnlNo}


            	and userId = #{userId}


            	and carno = #{carno}


            	and spaceno = #{spaceno}


            	and billStatus = #{billStatus}


            	 and createTime >= #{createTime} 

        	 order by createTime desc
