mapper.update:
update ParkingSpace set
		parkStatus   = 'N',
		modifyBy     = #{modifyBy},
		modifyTime   = #{modifyTime}
where comid = #{comid}

mapper.select:
		select * from ParkingSpace where 1=1

            	and spaceno = #{spaceno}


            	and comid = #{comid}


            	and parkPositionFloor = #{parkPositionFloor}


            	and parkPositionZone = #{parkPositionZone}


            	and parkPositionX = #{parkPositionX}


            	and parkPositionY = #{parkPositionY}


            	and parkStatus = #{parkStatus}


            	and parkType = #{parkType}



            	and parkPositionDes like '%'||#{parkPositionDes}||'%'


            	and spaceOwner = #{spaceOwner}


            	 and createTime >= #{createTime} 

        	 order by createTime desc
