mapper.select:
select 
	,

from propertymgmtuser p,Baseuser u
where p.userId = u.userId
and   p.comid  = #{comid} 
and   p.userId = #{userId}

mapper.insert:
insert into propertymgmtuser(
		comid,
		userId
	) 
 values(
 		#{comid},
 		#{userId}
 	)

mapper.update:
update propertymgmtuser set
		isAdmin        = #{isAdmin},
		memo           = #{memo},
		modifyBy       = #{modifyBy},
		modifyTime     = #{modifyTime}
where comid = #{comid} and userId=#{userId}

update propertymgmtuser set
		isAdmin    = -1,
		modifyBy   = #{modifyBy},
		modifyTime = #{modifyTime}
where comid = #{comid} and userId=#{userId}

mapper.select:
		select 
			,

		from propertymgmtuser p,Baseuser u
		where p.userId = u.userId


            	and p.comid = #{comid}



            	and u.userId = #{userId}


            	and u.isAdmin = #{isAdmin}


            	 and u.createTime >= #{createTime} 

        order by u.createTime desc
