mapper.update:
update ShareConfig set
		isOpen     = -1,
		modifyBy   = #{modifyBy},
		modifyTime = #{modifyTime}
where   UUID = #{UUID}

mapper.select:
		select * from ShareConfig where 1=1

            	and UUID = #{UUID}


            	and spaceno = #{spaceno}


            	and isAllDay = #{isAllDay}


            	and isOpen = #{isOpen}



        	  	 and isOpen in

 						#{item}  



            	and shareType = #{shareType}


        	 	 and startTime <= #{queryTime} 
        	 	 and endTime >= #{queryTime} 



            	 and startDate <= #{queryTime} 
        	 	 and endDate >= #{queryTime} 


            	 and createTime >= #{createTime} 

        	 order by createTime desc
